sources.sass\\_components\\_navigation.scss:
@use '../_abstracts' as *;

.navigation {
    display: flex;
    position: fixed;
    flex-direction: row;
    width: 100%;
    padding: 0 $space-s;
    background-color: color(sands);
    right: 0;
    align-items: center;
    height: $navi;
    z-index: 50;

    &__list {
        display: flex;
        flex-direction: column;
        position: fixed;
        padding-top: 120px;
        top: 0;
        right: 0;
        line-height: 3rem;
        min-width: 275px; 
        height: 100vh;
        background: color(darksands);
        list-style: none;
        text-transform: uppercase;
        text-align: center;
        transition: transform .4s;
        transform: translateX(100%);
        box-shadow: 7px 0px 15px black;
        z-index: 50;
    }

    &__item-link {
        display: inline-block;
        padding: $space-xs 0;
        padding-left: $space-m;
        letter-spacing: 1px;
        @include fluid-font (3vw, 22px, 22px, 22px);
        color: color(darks);
        width: 100%;
        text-align: left;
    }
}

@include tablet {
    .navigation {
        justify-content: center;
        &__list {
            all: unset;
            display: flex;
            flex-direction: row;
            list-style: none;
            text-transform: uppercase;
        }

        &__item-link {
            display: inline-block;
            width: 100%;
            position: relative;
            padding: 0;
            margin: 0 $space-s;
            @include fluid-font (3vw, 20px, 20px, 18px);
            text-align: center;
            transition: color .3s;
            outline-color: color(darkgolds);

            &::before {
                content: '';
                position: absolute;
                left: 0;
                bottom: 0;
                width: 100%;
                height: 2px;
                background-color: color(darks);
                transform: scaleX(0);
                transition: transform .3s, background-color .3s;
                z-index: -1;
            }

            &:hover {
                color: color(darks);
            }

            &:hover::before {
                transform: scaleX(0.7);
                background-color: color(darks);
            }
        }
    }
}

@include mobile-small {
    .navigation {
        &__list {
            padding-top: $navi;
        }
    }
}

sources.sass\\_components\\_hero.scss:
@use '../_abstracts/index' as *;

.hero {
    
    &__image {
        position: absolute;
        object-fit: cover;
        bottom: 0px;
        right: 0;
        height: 380px;
        width: 80%;
        z-index: -1;
    }
}

