    }
    
    & div {
        overflow: hidden;
    }

    .title__span {
        display: block;
        overflow: hidden;
    }
}

@include tablet {
    .hero {
        
        &__image {
            all: unset;
            position: absolute;
            right: 0;
            bottom: 0;
            height: 500px;
            width: 50%;
            object-fit: cover;
            object-position: center;
            z-index: -1;
        }
    }
