            width: 50%;
        }
    }

    .textbox {
        position: relative;
        bottom: 0;
        align-self: center;
    }
}

@include desktop {
    .hero {

        &__image {
            height: 85vh;
            max-height: 900px;
            max-width: none;
            width: 60%;
        }
    }
}

@include mobile-small {
    .textbox {

        &__title {
            @include fluid-font (5vw, 36px, 64px, 40px);
        }
        
    }
}

sources.sass\\_components\\_buttons.scss:
