        @include btn-color(color(darks), color(whites));
    }
}

@include tablet {
    .btn {
        margin: $space-m 0;
        width: 165px;

        &:hover {
            @include btn-color(color(darkgolds), color(lights));
        }

        &__secondary {
            width: 280px;
            height: 50px;
            line-height: 50px;
        }
    }
}

@include ipad-pro {
    .btn {
        &__secondary {
            width: 360px;
        }
    }
}

@media (hover: hover) and (pointer: fine) {
    .btn {
        &:hover {
            @include btn-color(color(darkgolds), color(lights));
        }
    }
}

