sources.sass\\_layouts\\_products.scss:
@use '../_abstracts/index' as *;    

.product {
    position: relative;
    display: grid;
    grid-template-columns: 5fr 1fr;
    padding: $space-xxs 0;

    &__container {
        display: flex;
        flex-direction: column;
        width: 100%;
        list-style: none;
    }

    &__subtitle {
        font-family: $font-secondary;
        @include fluid-font (1vw, 20px, 24px, 22px);
    }

    &__type {
        text-transform: lowercase;
        font-weight: normal;
        font-family: $font-special;

        &--green {
            color: color(greens);
        }

        &--red {
            color: #B51F24;
        }
        
        &--brown {
            color: #726817;
        }
    }

    &__subtitle {
        text-transform: uppercase;
        text-align: center;
        padding: $space-s 0;
    }
}

.special {
    &__container {
        display: grid;
        grid-template-columns: 5fr 1fr;
        padding: $space-xs 0;
    }
    &__size {
        display: inline;
    }

    &__two {
        display: none;
    }
}

.price {
    &__container {
        display: flex;
        justify-content: center;
        gap: 8px;

        &--drinks {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            grid-auto-flow: row;
        }
    }

    &__size {
        font-size: 24px;
    }
}

.add {
    &__product {
        display: flex;
        flex-direction: column;
    }

    &__container {
        display: grid;
        grid-template-columns: 5fr 1fr;
        justify-content: space-between;
    }

    &__prices {
        display: grid;
        grid-template-columns: repeat(2,1fr);
        justify-items: center;

        &--single {
            grid-template-columns: 1fr;
        }
    }
}

@include tablet {

    .product {
        padding: $space-xxs $space-xs;
    }

    .special {
        &__container {
            padding: $space-xs;
        }
    }

    .price {
        &__container {
            gap: 16px;
        }
    }
}

@include ipad-pro {
    .product {
        padding: $space-s $space-l;
        
        &__container {
            display: grid;
            width: 100%;
            height: 100%;
            grid-template-columns: repeat(2, 1fr);
        }

        &__subtitle {
            grid-column: 1/3;
            padding: $space-m 0;
        }
    }

    .special {
        &__container {
            padding: $space-s $space-l;
        }
        &__two {
            display: grid;
        }
    }
}

sources.sass\\_layouts\\_info.scss:
@use '../_abstracts/index' as *;

.info {
    &__title {
        text-transform: capitalize;
    }

    &__container {
        padding: 0 0 $space-s 0;

        &--phone {
            display: flex;
        }
    }

    &__container > div:first-child {
        padding-right: $space-m;
    }

    &__link {
        color: color(darkgolds);
        transition: color .3s;

        &:hover {
            color: color(golds);
        }
    }

}

@include tablet {
    .info {
        width: 100%;
        display: grid;
        grid-template-columns: 1fr 1fr;
    }
}

@include ipad-pro {
    .info {

        &__name {
            max-width: 500px;
        }
    }
}

@include desktop {
    .info {
    }
}

sources.sass\\_layouts\\_grid.scss:
@use '../_abstracts/index' as *;

.grid {
    &__container {
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        grid-template-rows: repeat (6, 1fr );
        place-items: center;
        gap: 2px;
        padding: 2px;
    }
}

.image {
    position: relative;
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    transition: transform .5s, filter .2s;
    
    &:hover {
        transform: scale(1.05);
        filter: brightness(110%);
    }

    &__container {
        overflow: hidden;
        width: 100%;
        height: 100%;
    }
}

#one {
    grid-column: 1 / 2;
    grid-row: 1;
}

#two {
    grid-column: 2 / 3;
    grid-row: 1;
}

#three {
    grid-column: 1 / 2;
    grid-row: 2;
}

#four {
    grid-column: 2 / 3;
    grid-row: 2;
}

#five {
    grid-column: 1 / 3;
    grid-row: 3;
}

#six {
    grid-column: 1 / 2;
    grid-row: 4;
}

#seven {
    grid-column: 2 / 3;
    grid-row: 4;
}

#eight {
    grid-column: 1 / 3;
    grid-row: 5;
}

@include tablet {
    .grid {
        &__container {
            grid-template-columns: repeat(3, 1fr);
            grid-template-rows: repeat (7, 1fr);
            gap: 8px;
            padding: 8px;
        }
    }

    #one {
        grid-column: 1 / 2;
        grid-row: 1;
    }

    #two {
        grid-column: 2 / 3;
        grid-row: 1;
    }

    #three {
        grid-column: 3 / 4;
        grid-row: 1;
    }

    #four {
        grid-column: 1 / 2;
        grid-row: 2 / 3;
    }

    #five {
        grid-column: 2 / 4;
        grid-row: 2 / 3;
    }

    #six {
        grid-column: 1 / 2;
        grid-row: 3;
    }

    #seven {
        grid-column: 2 / 3;
        grid-row: 3;
    }

    #eight {
        grid-column: 3 / 4;
        grid-row: 3;
    }
}

sources.sass\\_layouts\\_showcase.scss:
@use '../_abstracts/index' as *;

.showcase {
    display: flex;
    flex-direction: column;

    &__image-container {
        padding-bottom: $space-l;
    }

    &__image-wrapper {
        position: relative;
    }

    &__image {
        object-position: center;
        object-fit: cover;
        width: 100%;
    }

    &__image-decoration {
        display: none;
        position: absolute;
        top: -40px;
        left: -40px;
        width: 100%;
        height: 100%;
        background-color: color(darksands);
        z-index: -1;
    }

    &__title {
        position: relative;
        @include fluid-font (4.5vw, 36px, 42px, 42px);
        font-family: $font-secondary;
        text-transform: uppercase;
        font-weight: normal;
    }

    &__info {
        padding-top: $space-m;
    }

    &__icons {
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 16px;
        padding-top: $space-m;
    }

    &__icons-container {
        display: flex;
        flex-direction: column;
    }

    &__icon {
        margin: 0 auto;
        font-size: 36px;
        margin-top: $space-s;
    }

    &__icon-description {
        @include fluid-font (1.5vw, 16px, 20px, 20px);
        display: inline-block;
        padding: $space-xs 0;
        text-align: center;
        text-transform: capitalize;
    }
}

@include ipad-pro {
    .showcase {
        flex-direction: row;
        padding-top: $space-l;

        &__image-container {
            flex: 1;
            margin-top: $space-s;
            padding: 0 $space-m;
        }

        &__content {
            flex: 1;
            padding: 0 $space-m;
        }

        &__image {
            height: 650px;
        }

        &__image-decoration {
            display: block;
        }

        &__info {
            padding-top: $space-xl;
        }

        &__icons {
            margin-top: $space-xl;
            gap: 36px;
        }

        &__icon {
            font-size: 48px;
        }
    }
}

@include laptop {
    .showcase {
        &__image-container {
            padding: 0 $space-xxl;
        }
        &__content {
            padding: 0 $space-xxl;
        }
    }
}

sources.sass\\_layouts\\_prompt.scss:
@use '../_abstracts/index' as *;    

.prompt {
    width: 100%;
    margin-top: $space-l;
    padding: $space-l 0;
    background-color: color(darksands);

    &__container {
        display: flex;
        flex-direction: row;
        justify-content: space-around;
        align-items: center;
    }

    &__info {
        display: none;
        @include fluid-font (1.5vw, 36px, 42px, 42px);
        font-family: $font-primary;
        text-transform: none;
    }
}

@include ipad-pro {
    .prompt {
        margin-top: $space-xxxl;
        padding: $space-xxl 0;

        &__info {
            display: block;
        }
    }
}

sources.sass\\_abstracts\\_mixins\\_wrapper.scss:
@mixin wrapper {
    max-width: 1440px;
    margin: 0 auto;
    padding: 0 32px;

    @media (max-width: 320px) {
        padding: 0 12px;
    }

    @media (max-width: 768px) {
        padding: 0 16px;
    }

    @media (min-width: 768px) {
        padding: 0 24px;
    }

    @media (min-width: 1024px) {
        padding: 0 32px;
    }
}

.wrapper {
    @include wrapper;

    &__big {
        max-width: 1920px;
    }
}

@mixin container {
    max-width: 1440px;
    margin: 0 auto;
}

.container {
    @include container;
}

